  FROM movie
 WHERE movie_no = #{movieNo}

SELECT still4_url
  FROM movie
 WHERE movie_no = #{movieNo}

SELECT ROUND(AVG(scores), 1) as score 
  FROM review 
 WHERE movie_no = #{movieNo};
